import React, { useState } from 'react';
import './PostcardCanvas.scss';

interface PostcardData {
  id: number;
  city: string;
  name: string;
  imageUrl: string;
  xPos: number;
  yPos: number;
  rotation: number;
}

const PostcardCanvas = () => {
  const [cards, setCards] = useState<PostcardData[]>([]);
  const [formData, setFormData] = useState({ city: '', name: '', imageUrl: '' });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    if (!formData.city || !formData.name) return;
    const newCard: PostcardData = {
      id: Date.now(),
      city: formData.city,
      name: formData.name,
      imageUrl: formData.imageUrl || 'https://picsum.photos/400/600',
      xPos: Math.random() * 75, // Adjusted to keep cards fully on board
      yPos: Math.random() * 50,
      rotation: (Math.random() - 0.5) * 40 // Increased rotation for messier look
    };
    setCards([...cards, newCard]);
    setFormData({ city: '', name: '', imageUrl: '' });
  };

  // Reusable Card Component for both Canvas and Preview
  const CardItem = ({ data, isPreview = false }: { data: any, isPreview?: boolean }) => (
      <div 
        className={`postcard-item ${!isPreview ? 'on-canvas' : ''}`}
        style={!isPreview ? {
          left: `${data.xPos}%`,
          top: `${data.yPos}%`,
          transform: `rotate(${data.rotation}deg)`,
          zIndex: Math.floor(data.yPos)
        } : {}}
      >
         <div 
           className="postcard-image" 
           style={{ backgroundImage: `url(${data.imageUrl || 'https://via.placeholder.com/400x600?text=Image+URL'})` }} 
         />
         <div className="postcard-text">
           <div className="city-name">{data.city || "SAN FRANCISCO"}</div>
           <div className="user-name">- {data.name || "Shailee"}</div>
         </div>
      </div>
  );

  return (
    <div className="postcard-canvas-container">
      <h2 className="canvas-title">Postcards from Around the World</h2>

      <div className="canvas-board">
        {cards.length === 0 && <div className="canvas-placeholder-text">Global Guestbook Empty</div>}
        {cards.map((card) => <CardItem key={card.id} data={card} />)}
      </div>

      <div className="creation-zone">
        <div className="preview-container">
           <h3>Live Preview</h3>
           <CardItem data={formData} isPreview={true} />
        </div>

        <div className="form-container">
            <input name="city" className="modern-input" placeholder="City Name" 
                   value={formData.city} onChange={handleInputChange} maxLength={18}/>
            <input name="name" className="modern-input" placeholder="Your Name" 
                   value={formData.name} onChange={handleInputChange} maxLength={15}/>
            <input name="imageUrl" className="modern-input" placeholder="Image URL (optional)" 
                   value={formData.imageUrl} onChange={handleInputChange}/>
            <button className="submit-btn" onClick={handleSubmit} disabled={!formData.city || !formData.name}>
                Stick to Board
            </button>
        </div>
      </div>
    </div>
  );
};

export default PostcardCanvas;
