import React, { useState, useEffect } from 'react';
import './PostcardCanvas.scss';

interface PostcardData {
  id: number;
  city: string;
  name: string;
  imageUrl: string;
  message: string;
  xPos: number;
  yPos: number;
  rotation: number;
}

const PostcardCanvas = () => {
  const [cards, setCards] = useState<PostcardData[]>([]);
  const [formData, setFormData] = useState({ city: '', name: '', imageUrl: '', message: '' });
  const [isLoadingImage, setIsLoadingImage] = useState(false);
  const [autoGeneratedUrl, setAutoGeneratedUrl] = useState(false);

  // Auto-fetch image when location is entered and no URL is provided
  useEffect(() => {
    if (formData.city && !formData.imageUrl) {
      setIsLoadingImage(true);
      // Use a free image service that doesn't require API key
      // Using Picsum with location-based seed for consistent images per location
      const locationHash = formData.city.toLowerCase().split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
      const imageUrl = `https://picsum.photos/seed/${locationHash}/400/600`;

      // Set the image URL directly
      setFormData(prev => ({ ...prev, imageUrl }));
      setAutoGeneratedUrl(true);
      setIsLoadingImage(false);
    } else if (!formData.city && autoGeneratedUrl) {
      // Clear auto-generated image if location is cleared
      setFormData(prev => ({ ...prev, imageUrl: '' }));
      setAutoGeneratedUrl(false);
    }
  }, [formData.city]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const newValue = e.target.value;
    const fieldName = e.target.name;

    // If user manually enters imageUrl, clear the auto-generated flag
    if (fieldName === 'imageUrl' && newValue && autoGeneratedUrl) {
      setAutoGeneratedUrl(false);
    }

    setFormData({ ...formData, [fieldName]: newValue });
  };

  const [newCardAnimation, setNewCardAnimation] = useState<number | null>(null);

  const handleSubmit = () => {
    if (!formData.city || !formData.name || !formData.imageUrl) return;
    const newCard: PostcardData = {
      id: Date.now(),
      city: formData.city,
      name: formData.name,
      imageUrl: formData.imageUrl,
      message: formData.message || '',
      xPos: Math.random() * 75,
      yPos: Math.random() * 50,
      rotation: (Math.random() - 0.5) * 40
    };
    setCards([...cards, newCard]);
    setNewCardAnimation(newCard.id);
    setFormData({ city: '', name: '', imageUrl: '', message: '' });
    setAutoGeneratedUrl(false);

    // Reset animation after it completes
    setTimeout(() => setNewCardAnimation(null), 1000);
  };

  // Reusable Card Component
  const CardItem = ({ data, isPreview = false, isThumbnail = false }: { data: any, isPreview?: boolean, isThumbnail?: boolean }) => {
    const displayMessage = data.message ? (data.message.length > 300 ? data.message.substring(0, 300) + '...' : data.message) : '';
    const hasImage = data.imageUrl && data.imageUrl.trim() !== '';
    // Show tooltip above if card is in bottom 40% of canvas
    const showTooltipAbove = !isPreview && !isThumbnail && data.yPos && data.yPos > 60;
    const isNewCard = newCardAnimation === data.id;

    return (
      <div 
        className={`postcard-item ${!isPreview && !isThumbnail ? 'on-canvas' : isThumbnail ? 'thumbnail-card' : 'preview-card'} ${isNewCard ? 'new-card-animation' : ''}`}
        style={!isPreview && !isThumbnail ? {
          left: `${data.xPos}%`,
          top: `${data.yPos}%`,
          transform: `rotate(${data.rotation}deg)`,
          zIndex: Math.floor(data.yPos)
        } : {}}
      >
         <div 
           className="postcard-image" 
           style={hasImage ? { backgroundImage: `url(${data.imageUrl})` } : { backgroundColor: '#f0f0f0' }}
         >
           {!hasImage && <div className="no-image-placeholder">No Image</div>}
         </div>
         <div className="postcard-text">
           <div className="greetings-text">Greetings from</div>
           <div className="city-name">{data.city || "Location"}</div>
           <div className="user-name">{data.name ? data.name.toUpperCase() : "NAME"}</div>
         </div>
      </div>
    );
  };

  return (
    <div className="postcard-canvas-container">

      {/* HEADER AREA */}
      <div className="canvas-header">
        <div>
          <h2 className="canvas-title">Digital Guestbook</h2>
          <p className="canvas-subtitle">Leave your mark on my website</p>
        </div>
      </div>

      {/* MAIN CONTENT AREA */}
      <div className="main-content-area">

        {/* 3D POSTCARD FORM */}
        <div className="postcard-form-wrapper">
          <div className="postcard-form-3d">
            <div className="postcard-form-inner">
              <h3 className="form-title">Write Your Postcard</h3>
              <div className="form-fields">
                <input name="city" className="postcard-input" placeholder="üìç Location *" 
                        value={formData.city} onChange={handleInputChange} maxLength={18}/>
                <input name="name" className="postcard-input" placeholder="‚úçÔ∏è Your Name *" 
                        value={formData.name} onChange={handleInputChange} maxLength={15}/>
                <input name="imageUrl" className="postcard-input" placeholder="üñºÔ∏è Image URL *" 
                        value={formData.imageUrl} onChange={handleInputChange}/>
                {isLoadingImage && <div className="loading-text">Loading image for location...</div>}
                <textarea name="message" className="postcard-input message-input" placeholder="üí¨ Your Message..." 
                        value={formData.message} onChange={handleInputChange} maxLength={300} rows={4}/>
                <button className="postcard-submit-btn" onClick={handleSubmit} disabled={!formData.city || !formData.name || !formData.imageUrl}>
                    ‚úâÔ∏è Send Postcard
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* GUESTBOOK GALLERY */}
        <div className="guestbook-gallery">
          <h3 className="gallery-title">
            <span className="gallery-icon">üì¨</span>
            Messages from Visitors
            <span className="card-count">({cards.length})</span>
          </h3>
          {cards.length === 0 ? (
            <div className="empty-guestbook">
              <div className="empty-icon">üì≠</div>
              <p>No messages yet. Be the first to leave a postcard!</p>
            </div>
          ) : (
            <div className="guestbook-grid">
              {cards.map((card, index) => (
                <div 
                  key={card.id} 
                  style={{ '--index': index } as React.CSSProperties}
                  className="grid-item-wrapper"
                >
                  <CardItem data={card} isThumbnail={true} />
                </div>
              ))}
            </div>
          )}
        </div>

      </div>
    </div>
  );
};

export default PostcardCanvas;
