import React, { useState, useEffect } from 'react';
import './PostcardCanvas.scss';

interface PostcardData {
  id: number;
  city: string;
  name: string;
  imageUrl: string;
  message: string;
  xPos: number;
  yPos: number;
  rotation: number;
}

const PostcardCanvas = () => {
  const [cards, setCards] = useState<PostcardData[]>([]);
  const [formData, setFormData] = useState({ city: '', name: '', imageUrl: '', message: '' });
  const [isLoadingImage, setIsLoadingImage] = useState(false);

  // Auto-fetch image when location is entered and no URL is provided
  useEffect(() => {
    if (formData.city && !formData.imageUrl) {
      const fetchLocationImage = async () => {
        setIsLoadingImage(true);
        try {
          // Using Unsplash API to get location-based image
          const response = await fetch(
            `https://api.unsplash.com/photos/random?query=${encodeURIComponent(formData.city)}&orientation=portrait&client_id=YOUR_UNSPLASH_ACCESS_KEY`
          );
          if (response.ok) {
            const data = await response.json();
            setFormData(prev => ({ ...prev, imageUrl: data.urls.regular }));
          } else {
            // Fallback to a placeholder service if Unsplash fails
            setFormData(prev => ({ 
              ...prev, 
              imageUrl: `https://source.unsplash.com/400x600/?${encodeURIComponent(formData.city)}` 
            }));
          }
        } catch (error) {
          // Fallback to placeholder
          setFormData(prev => ({ 
            ...prev, 
            imageUrl: `https://source.unsplash.com/400x600/?${encodeURIComponent(formData.city)}` 
          }));
        } finally {
          setIsLoadingImage(false);
        }
      };

      // Debounce the API call
      const timer = setTimeout(() => {
        fetchLocationImage();
      }, 500);

      return () => clearTimeout(timer);
    }
  }, [formData.city]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    if (!formData.city || !formData.name || !formData.imageUrl) return;
    const newCard: PostcardData = {
      id: Date.now(),
      city: formData.city,
      name: formData.name,
      imageUrl: formData.imageUrl,
      message: formData.message || '',
      xPos: Math.random() * 75,
      yPos: Math.random() * 50,
      rotation: (Math.random() - 0.5) * 40
    };
    setCards([...cards, newCard]);
    setFormData({ city: '', name: '', imageUrl: '', message: '' });
  };

  // Reusable Card Component
  const CardItem = ({ data, isPreview = false, cardIndex = 0 }: { data: any, isPreview?: boolean, cardIndex?: number }) => {
    const displayMessage = data.message ? (data.message.length > 300 ? data.message.substring(0, 300) + '...' : data.message) : '';
    const hasImage = data.imageUrl && data.imageUrl.trim() !== '';

    return (
      <div 
        className={`postcard-item ${!isPreview ? 'on-canvas' : 'preview-card'}`}
        style={!isPreview ? {
          left: `${data.xPos}%`,
          top: `${data.yPos}%`,
          transform: `rotate(${data.rotation}deg)`,
          zIndex: Math.floor(data.yPos)
        } : {}}
      >
         <div 
           className="postcard-image" 
           style={hasImage ? { backgroundImage: `url(${data.imageUrl})` } : { backgroundColor: '#f0f0f0' }}
         >
           {!hasImage && <div className="no-image-placeholder">No Image</div>}
         </div>
         <div className="postcard-text">
           <div className="city-name">{data.city || "Location"}</div>
           <div className="user-name">- {data.name || "Name"}</div>
         </div>
         {!isPreview && displayMessage && (
           <div className={`postcard-message-hover ${cardIndex > 0 ? 'show-above' : ''}`}>
             {displayMessage}
           </div>
         )}
      </div>
    );
  };

  return (
    <div className="postcard-canvas-container">

      {/* HEADER AREA */}
      <div className="canvas-header">
        <div>
          <h2 className="canvas-title">Thanks for visiting my website!</h2>
          <p className="canvas-subtitle">Leave a note for me below</p>
        </div>
      </div>

      {/* NEW FORM CONTAINER (TOP) */}
      <div className="form-container">
        <input name="city" className="modern-input" placeholder="Location" 
                value={formData.city} onChange={handleInputChange} maxLength={18}/>
        <input name="name" className="modern-input" placeholder="Your Name" 
                value={formData.name} onChange={handleInputChange} maxLength={15}/>
        <input name="imageUrl" className="modern-input" placeholder="Image URL (optional)" 
                value={formData.imageUrl} onChange={handleInputChange}/>
        <textarea name="message" className="modern-input message-input" placeholder="Message" 
                value={formData.message} onChange={handleInputChange} maxLength={300} rows={3}/>
        <button className="submit-btn" onClick={handleSubmit} disabled={!formData.city || !formData.name}>
            Stick to Board
        </button>
      </div>

      {/* NEW MAIN CONTENT AREA (Canvas + Preview) */}
      <div className="main-content-area">

        {/* CANVAS (LEFT) */}
        <div className="canvas-board">
          {cards.length === 0 && <div className="canvas-placeholder-text">No messages yet. Be the first to leave a note!</div>}
          {cards.map((card) => <CardItem key={card.id} data={card} />)}
        </div>

        {/* PREVIEW (RIGHT) */}
        <div className="preview-container">
           <h3>Live Preview</h3>
           <CardItem data={formData} isPreview={true} />
        </div>

      </div>
    </div>
  );
};

export default PostcardCanvas;
