import React, { useState } from 'react';
import './PostcardCanvas.scss';

interface PostcardData {
  id: number;
  city: string;
  name: string;
  imageUrl: string;
  message: string;
  xPos: number;
  yPos: number;
  rotation: number;
}

const PostcardCanvas = () => {
  const [cards, setCards] = useState<PostcardData[]>([]);
  const [formData, setFormData] = useState({ city: '', name: '', imageUrl: '', message: '' });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    if (!formData.city || !formData.name) return;
    const newCard: PostcardData = {
      id: Date.now(),
      city: formData.city,
      name: formData.name,
      imageUrl: formData.imageUrl || 'https://picsum.photos/400/600',
      message: formData.message,
      // Randomizing position within the "white frame"
      xPos: Math.random() * 70 + 5, 
      yPos: Math.random() * 60 + 5,
      rotation: (Math.random() - 0.5) * 30
    };
    setCards([...cards, newCard]);
    setFormData({ city: '', name: '', imageUrl: '', message: '' });
  };

  const CardItem = ({ data, isPreview = false }: { data: any, isPreview?: boolean }) => {
    const displayMessage = data.message ? (data.message.length > 300 ? data.message.substring(0, 300) + '...' : data.message) : '';

    return (
      <div 
        className={`postcard-item ${isPreview ? 'preview-mode' : 'on-canvas'}`}
        style={!isPreview ? {
          left: `${data.xPos}%`,
          top: `${data.yPos}%`,
          transform: `rotate(${data.rotation}deg)`,
          zIndex: Math.floor(data.yPos)
        } : {}}
      >
         <div 
           className="postcard-image" 
           style={{ backgroundImage: `url(${data.imageUrl || 'https://picsum.photos/400/600'})` }} 
         />
         <div className="postcard-text">
           <div className="city-name">{data.city || "CITY NAME"}</div>
           <div className="user-name">- {data.name || "Your Name"}</div>
         </div>
         {!isPreview && displayMessage && (
           <div className="postcard-message-hover">
             {displayMessage}
           </div>
         )}
      </div>
    );
  };

  return (
    <div className="postcard-canvas-container">
      <div className="canvas-header">
        <div>
          <h2 className="canvas-title">Postcard Gallery</h2>
          <p className="canvas-subtitle">Leave a note from your corner of the world</p>
        </div>
      </div>

      <div className="main-content-area">
        {/* THE WHITE FRAME CANVAS */}
        <div className="canvas-board">
          {cards.length === 0 && <div className="canvas-placeholder-text">The frame is empty. Be the first!</div>}
          {cards.map((card) => <CardItem key={card.id} data={card} />)}
        </div>

        {/* 3D FORM SIDEBAR */}
        <div className="postcard-form-wrapper">
          <div className="postcard-form-3d">
            <h3 className="form-title">Write a Postcard</h3>

            <div className="preview-mini-section">
                <CardItem data={formData} isPreview={true} />
            </div>

            <div className="postcard-form-body">
              <input name="city" className="postcard-input" placeholder="Location (e.g. New York)" 
                      value={formData.city} onChange={handleInputChange} maxLength={18}/>
              <input name="name" className="postcard-input" placeholder="Your Name" 
                      value={formData.name} onChange={handleInputChange} maxLength={15}/>
              <input name="imageUrl" className="postcard-input" placeholder="Image URL (optional)" 
                      value={formData.imageUrl} onChange={handleInputChange}/>
              <textarea name="message" className="postcard-textarea" placeholder="Your message..." 
                      value={formData.message} onChange={handleInputChange} maxLength={300} rows={3}/>

              <button className="postcard-submit-btn" onClick={handleSubmit} disabled={!formData.city || !formData.name}>
                Pin to Board
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostcardCanvas;
