import React, { useState } from 'react';
import './PostcardCanvas.scss';

interface PostcardData {
  id: number;
  city: string;
  name: string;
  imageUrl: string;
  xPos: number; // 0-100%
  yPos: number; // 0-100%
  rotation: number;
}

const PostcardCanvas = () => {
  const [cards, setCards] = useState<PostcardData[]>([]);
  const [formData, setFormData] = useState({ city: '', name: '', imageUrl: '' });
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // --- GEOCODING HELPER ---
  const fetchCoordinates = async (cityName: string) => {
    try {
      // Using OpenStreetMap's free Nominatim API
      const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(cityName)}&limit=1`);
      const data = await response.json();

      if (data && data.length > 0) {
        return {
          lat: parseFloat(data[0].lat),
          lon: parseFloat(data[0].lon)
        };
      }
      return null;
    } catch (error) {
      console.error("Geocoding failed:", error);
      return null;
    }
  };

  const handleSubmit = async () => {
    if (!formData.city || !formData.name) return;

    setIsLoading(true);
    const coords = await fetchCoordinates(formData.city);
    setIsLoading(false);

    // Default positions (center of map) if geocoding fails
    let xPos = 50;
    let yPos = 50;

    if (coords) {
      // Convert Lat/Lon to X/Y percentages for Equirectangular projection
      // Longitude (-180 to 180) -> X (0 to 100%)
      xPos = (coords.lon + 180) / 360 * 100;

      // Latitude (90 to -90) -> Y (0 to 100%)
      // Note: Map Y goes 0 (top) to 100 (bottom), so 90 lat is 0% Y.
      yPos = (90 - coords.lat) / 180 * 100;

      // Add a tiny bit of random jitter so cards from same city don't perfectly overlap
      xPos += (Math.random() - 0.5) * 2;
      yPos += (Math.random() - 0.5) * 2;
    }

    const newCard: PostcardData = {
      id: Date.now(),
      city: formData.city,
      name: formData.name,
      imageUrl: formData.imageUrl || 'https://picsum.photos/400/600',
      xPos,
      yPos,
      rotation: (Math.random() - 0.5) * 30
    };

    setCards([...cards, newCard]);
    setFormData({ city: '', name: '', imageUrl: '' });
  };

  const handleReset = () => setCards([]);

  const CardItem = ({ data, isPreview = false }: { data: any, isPreview?: boolean }) => (
      <div 
        className={`postcard-item ${!isPreview ? 'on-canvas' : ''}`}
        style={!isPreview ? {
          left: `${data.xPos}%`,
          top: `${data.yPos}%`,
          // We combine the centering translate with the random rotation
          transform: `translate(-50%, -50%) rotate(${data.rotation}deg)`,
          zIndex: Math.floor(data.yPos)
        } : {}}
      >
         <div 
           className="postcard-image" 
           style={{ backgroundImage: `url(${data.imageUrl || 'https://via.placeholder.com/400x600?text=Image+URL'})` }} 
         />
         <div className="postcard-text">
           <div className="city-name">{data.city || "SAN FRANCISCO"}</div>
           <div className="user-name">- {data.name || "Shailee"}</div>
         </div>
      </div>
  );

  return (
    <div className="postcard-canvas-container">
      <div className="canvas-header">
        <h2 className="canvas-title">Postcards from Around the World</h2>
        <button className="reset-btn" onClick={handleReset}>Clear Map</button>
      </div>

      <div className="form-container">
        <input name="city" className="modern-input" placeholder="City Name" 
               value={formData.city} onChange={handleInputChange} maxLength={18}/>
        <input name="name" className="modern-input" placeholder="Your Name" 
               value={formData.name} onChange={handleInputChange} maxLength={15}/>
        <input name="imageUrl" className="modern-input" placeholder="Image URL (optional)" 
               value={formData.imageUrl} onChange={handleInputChange}/>
        <button className="submit-btn" onClick={handleSubmit} disabled={!formData.city || !formData.name || isLoading}>
            {isLoading ? 'Locating...' : 'Stick to Map'}
        </button>
      </div>

      <div className="main-content-area">
        <div className="canvas-board">
          {cards.length === 0 && <div className="canvas-placeholder-text">Map is Empty. Add your travels!</div>}
          {cards.map((card) => <CardItem key={card.id} data={card} />)}
        </div>

        <div className="preview-container">
           <h3>Live Preview</h3>
           <CardItem data={formData} isPreview={true} />
        </div>
      </div>
    </div>
  );
};

export default PostcardCanvas;
