import React, { useState } from "react";
import '../assets/styles/CareerBento.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faMinus, faLocationArrow } from '@fortawesome/free-solid-svg-icons';

const CareerBento = () => {
  const [expandedIndex, setExpandedIndex] = useState<number | null>(0);

  const history = [
    {
      org: "KLAW Industries",
      role: "Software Engineer Intern",
      date: "2024 — 2025",
      location: "Binghamton, NY",
      logo: "https://images.squarespace-cdn.com/content/v1/5cfee7779de45a000197fd31/1622649420794-QC687JBOL698CEBAZOTH/KLAW+Industries+Transparent.png",
      description: "Designed low-latency telemetry ingestion services and built scalable video pipelines for multi-sensor data."
    },
    {
      org: "Binghamton University",
      role: "MS in Computer Science",
      date: "2023 — 2025",
      location: "New York, USA",
      logo: "https://upload.wikimedia.org/wikipedia/en/6/67/State_University_of_New_York_at_Binghamton_Seal.png",
      description: "Specialization in Data Science and Systems with a 3.9 GPA. Served as a Graduate Assistant."
    },
    {
      org: "LTIMindtree",
      role: "Software Engineer",
      date: "2021 — 2023",
      location: "Mumbai, India",
      logo: "https://companieslogo.com/img/orig/LTIM.NS-dea59dc6.png?t=1720244492",
      description: "Optimized financial microservices reducing latency by 65% and migrated legacy systems to Drools engines."
    }
  ];

  return (
    <section className="career-minimal-section" id="career">
      <div className="line-divider"></div>
      <div className="career-list">
        {history.map((item, index) => (
          <div 
            key={index} 
            className={`career-row ${expandedIndex === index ? 'active' : ''}`}
            onMouseEnter={() => setExpandedIndex(index)}
          >
            <div className="row-main">
              <span className="row-index">0{index + 1}</span>
              <div className="row-title">
                <h3>{item.role}</h3>
                <p>{item.org}</p>
              </div>
              <span className="row-date">{item.date}</span>
              <div className="row-icon">
                <FontAwesomeIcon icon={expandedIndex === index ? faMinus : faPlus} />
              </div>
            </div>

            <div className="row-details">
              <div className="details-inner">
                <div className="details-text">
                  <p>{item.description}</p>
                  <div className="details-meta">
                    <span><FontAwesomeIcon icon={faLocationArrow} /> {item.location}</span>
                  </div>
                </div>
                <div className="details-media">
                  <img src={item.logo} alt="logo" />
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default CareerBento;
