import React, { useState } from 'react';
import './GridEntry.scss';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';

const f1Data: Record<string, string[]> = {
  McLaren: ["Lando Norris", "Oscar Piastri"],
  Ferrari: ["Charles Leclerc", "Lewis Hamilton"],
  "Red Bull": ["Max Verstappen", "Isack Hadjar"],
  Mercedes: ["George Russell", "Kimi Antonelli"],
  "Aston Martin": ["Fernando Alonso", "Lance Stroll"],
  Audi: ["Nico Hülkenberg", "Gabriel Bortoleto"],
  Williams: ["Alex Albon", "Carlos Sainz"],
  Alpine: ["Pierre Gasly", "Franco Colapinto"],
  Haas: ["Esteban Ocon", "Oliver Bearman"],
  "Racing Bulls": ["Liam Lawson", "Arvid Lindblad"],
  Cadillac: ["Sergio Pérez", "Valtteri Bottas"]
};

const voterData = [
  { id: 1, name: "Shailee", team: "McLaren", driver: "Lando Norris" },
];

const GridEntry = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({ name: '', team: '', driver: '' });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="grid-entry-container" id="contact">
      <div className="main-content-area">
        <div className={`f1-terminal ${step === 3 ? 'wide' : ''}`}>

          {/* STEP 1 */}
          {step === 1 && (
            <div className="terminal-content">
              <h2 className="terminal-header">{'>'} MISSION_OBJECTIVE</h2>
              <p className="objective-text">
                Formula 1 represents the intersection of performance, precision, and relentless optimization.
                The Prediction Wall invites you to step into the pit lane and place your call for the 2026 championship —
                where data meets instinct.
              </p>
              <div className="actions">
                <button onClick={() => setStep(2)} className="btn-primary">
                  JOIN THE GRID <ArrowForwardIosIcon fontSize="inherit" />
                </button>
                <button onClick={() => setStep(3)} className="btn-secondary">
                  VIEW PIT WALL
                </button>
              </div>
            </div>
          )}

          {/* STEP 2 */}
          {step === 2 && (
            <div className="terminal-content">
              <button onClick={() => setStep(1)} className="btn-back">
                <ArrowBackIosNewIcon fontSize="inherit" /> BACK
              </button>
              <h2 className="terminal-header">{'>'} STRATEGIST_REGISTRATION</h2>

              <div className="form-grid">
                <div className="input-group">
                  <label>USER_ID</label>
                  <input name="name" value={formData.name} onChange={handleInputChange} />
                </div>
                <div className="input-group">
                  <label>CONSTRUCTOR</label>
                  <select name="team" value={formData.team} onChange={handleInputChange}>
                    <option value="">SELECT TEAM</option>
                    {Object.keys(f1Data).map(team => (
                      <option key={team}>{team}</option>
                    ))}
                  </select>
                </div>
                <div className="input-group">
                  <label>PILOT</label>
                  <select name="driver" value={formData.driver} onChange={handleInputChange} disabled={!formData.team}>
                    <option value="">SELECT DRIVER</option>
                    {formData.team &&
                      f1Data[formData.team].map(driver => (
                        <option key={driver}>{driver}</option>
                      ))}
                  </select>
                </div>
                <button onClick={() => setStep(3)} className="btn-primary">
                  LOCK STRATEGY
                </button>
              </div>
            </div>
          )}

          {/* STEP 3: PIT WALL RESULTS */}
          {step === 3 && (
            <div className="terminal-content">
              <div className="pit-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
                <h2 className="terminal-header" style={{ margin: 0 }}>{'>'} PIT_WALL_TELEMETRY</h2>

                {/* Updated Back Button to support form navigation */}
                <button onClick={() => setStep(2)} className="btn-back" style={{ marginBottom: 0 }}>
                  <ArrowBackIosNewIcon fontSize="inherit" /> BACK TO REGISTRATION
                </button>
              </div>

              <div className="telemetry-grid">
                {Object.entries(f1Data).map(([team, drivers]) =>
                  drivers.map(driver => {
                    const votes = voterData.filter(v => v.driver === driver);
                    const pct = Math.min(votes.length * 20, 100);

                    return (
                      <div className="driver-card" key={driver}>
                        <div className="driver-meta">
                          <span className="team">{team}</span>
                          <span className="votes">{votes.length} V</span>
                        </div>

                        <div className="driver-main">
                          <img
                            src={`/drivers/${driver.replace(' ', '_')}.png`}
                            alt={driver}
                            onError={(e) => ((e.target as HTMLImageElement).style.display = 'none')}
                          />
                          <div className="name">{driver}</div>
                        </div>

                        <div className="supporters">
                          {votes.length
                            ? votes.map(v => <span key={v.id}>{v.name}</span>)
                            : <span className="muted">NO DATA</span>}
                        </div>

                        <div className="bar">
                          <div className="fill" style={{ width: `${pct}%` }} />
                        </div>
                      </div>
                    );
                  })
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default GridEntry;
