import React from "react";
import '../assets/styles/CareerBento.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt, faCalendarAlt, faExternalLinkAlt } from '@fortawesome/free-solid-svg-icons';

const CareerBento = () => {
  const history = [
    {
      org: "KLAW Industries",
      role: "Software Engineer Intern",
      date: "2024 — 2025",
      location: "Binghamton, NY",
      logo: "https://images.squarespace-cdn.com/content/v1/5cfee7779de45a000197fd31/1622649420794-QC687JBOL698CEBAZOTH/KLAW+Industries+Transparent.png",
      bullets: [
        "Designed telemetry ingestion services for multi-sensor data using AWS IoT Core.",
        "Built scalable video pipelines achieving sub-200ms end-to-end latency.",
        "Automated fault-tolerant Linux services for high-availability systems."
      ]
    },
    {
      org: "Binghamton University",
      role: "Master of Science in Computer Science",
      date: "2023 — 2025",
      location: "New York, USA",
      logo: "https://upload.wikimedia.org/wikipedia/en/6/67/State_University_of_New_York_at_Binghamton_Seal.png",
      bullets: [
        "Specialization in Data Science and Systems; GPA: 3.9/4.0.",
        "Relevant Coursework: Cloud Computing, Design Patterns, Network Security.",
        "Served as Graduate Assistant for the Computer Science department."
      ]
    },
    {
      org: "LTIMindtree",
      role: "Software Engineer",
      date: "2021 — 2023",
      location: "Mumbai, India",
      logo: "https://companieslogo.com/img/orig/LTIM.NS-dea59dc6.png?t=1720244492",
      bullets: [
        "Optimized financial microservices reducing latency by 65%.",
        "Migrated legacy PL/SQL business logic into Drools rule engines.",
        "Developed ETL services consolidating data from over 120 applications."
      ]
    },
    {
      org: "University of Mumbai",
      role: "Bachelor of Engineering in Computer Engineering",
      date: "2017 — 2021",
      location: "Mumbai, India",
      logo: "https://medcounsel.in/PG/images/1642173662.png",
      bullets: [
        "Graduated First Class with Distinction.",
        "Social Media Head at Alumni Cell and Senior Photographer for Shutterbugs.",
        "Published research paper on automated systems."
      ]
    }
  ];

  return (
    <section className="career-section" id="career">
      <div className="container">
        <h2 className="section-heading">Experience & Education</h2>

        <div className="history-list">
          {history.map((item, index) => (
            <div key={index} className="history-item">
              <div className="item-logo">
                <img src={item.logo} alt={item.org} />
              </div>

              <div className="item-content">
                <div className="item-header">
                  <div>
                    <h3>{item.role}</h3>
                    <h4 className="org-name">{item.org}</h4>
                  </div>
                  <div className="item-meta">
                    <span><FontAwesomeIcon icon={faCalendarAlt} /> {item.date}</span>
                    <span><FontAwesomeIcon icon={faMapMarkerAlt} /> {item.location}</span>
                  </div>
                </div>

                <ul className="item-bullets">
                  {item.bullets.map((bullet, i) => (
                    <li key={i}>{bullet}</li>
                  ))}
                </ul>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default CareerBento;
