import React, { useState, useEffect } from 'react';
import './Postcard.css';

const Postcard = () => {
  const [isFlipped, setIsFlipped] = useState(false);
  const [isSent, setIsSent] = useState(false);
  const [city, setCity] = useState('');
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [debouncedCity, setDebouncedCity] = useState('');

  // Handle the delay for the city image update (Debounce)
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedCity(city);
    }, 500);
    return () => clearTimeout(timer);
  }, [city]);

  const handleSend = () => {
    if (!city || !name) {
      alert("Please sign your name and city before sending!");
      return;
    }

    setIsFlipped(true);

    // Wait for flip, then fly away
    setTimeout(() => {
      setIsSent(true);
    }, 2000);
  };

  const handleReset = () => {
    setIsFlipped(false);
    setIsSent(false);
    setCity('');
    setName('');
    setMessage('');
  };

  return (
    <div className="postcard-container"> 
      {/* We added a container to keep it isolated from the rest of the portfolio styles */}
        <div className="card-scene">
            <div className={`postcard ${isFlipped ? 'flipped' : ''} ${isSent ? 'sent' : ''}`}>

                {/* Front (Image Side) */}
                <div className="card-face front">
                    <img 
                        src={`https://picsum.photos/seed/${debouncedCity || 'travel'}/500/300`} 
                        alt="City" 
                        className="front-image" 
                    />
                    <div className="city-label">
                        {debouncedCity.length > 2 ? `Greetings from ${debouncedCity}` : 'Greetings from...'}
                    </div>
                </div>

                {/* Back (Writing Side) */}
                <div className="card-face back">
                    <div className="stamp-placeholder">
                        Your Mandala
                    </div>

                    <div className="message-side">
                        <label className="form-label">Note:</label>
                        <textarea 
                            placeholder="Something cool about my city is..." 
                            maxLength={140}
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                        />
                        <button className="send-btn" onClick={handleSend}>
                            Stamp & Send Postcard
                        </button>
                    </div>

                    <div className="address-side">
                        <div className="form-group">
                            <label className="form-label">From (City):</label>
                            <input 
                                type="text" 
                                className="form-input" 
                                placeholder="e.g. Seattle" 
                                value={city}
                                onChange={(e) => setCity(e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label className="form-label">Your Name:</label>
                            <input 
                                type="text" 
                                className="form-input" 
                                placeholder="Standard Traveler"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>

        {isSent && (
            <button id="reset-btn" style={{display: 'block'}} onClick={handleReset}>
                Send Another
            </button>
        )}
    </div>
  );
};

export default Postcard;
