import React, { useEffect, useRef, useState } from 'react';
import './TrackLine.scss';

const TrackLine = () => {
  const pathRef = useRef<SVGPathElement>(null);
  const [carPos, setCarPos] = useState({ x: 0, y: 0, rotation: 90 });

  useEffect(() => {
    const handleScroll = () => {
      if (!pathRef.current) return;

      const path = pathRef.current;
      const pathLength = path.getTotalLength();

      // Calculate scroll percentage
      const scrollTotal = document.documentElement.scrollHeight - window.innerHeight;
      const scrollPercent = Math.min(Math.max(window.scrollY / scrollTotal, 0), 1);

      // Get the point on the SVG path
      const point = path.getPointAtLength(scrollPercent * pathLength);

      // Calculate rotation for the car to face forward
      const nextPoint = path.getPointAtLength(Math.min(pathLength, (scrollPercent + 0.01) * pathLength));
      const angle = Math.atan2(nextPoint.y - point.y, nextPoint.x - point.x) * (180 / Math.PI);

      setCarPos({ x: point.x, y: point.y, rotation: angle });
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); 
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="track-line-container">
      {/* viewBox="0 0 100 100" with preserveAspectRatio="none" 
        allows the curve to stretch the ENTIRE length of your site.
      */}
      <svg viewBox="0 0 100 100" preserveAspectRatio="none">
        <path
          ref={pathRef}
          className="track-path"
          d="M 20,0 
             C 80,20 80,30 20,50 
             S 20,80 80,100" 
          fill="none"
        />
      </svg>

      <div 
        className="moving-car"
        style={{ 
          left: `${carPos.x}%`, 
          top: `${carPos.y}%`, 
          transform: `translate(-50%, -50%) rotate(${carPos.rotation}deg)` 
        }}
      >
        üèéÔ∏è
      </div>
    </div>
  );
};

export default TrackLine;
