import React, { useEffect, useState } from "react";
import { Main, CareerBento, ProjectArchive, Navigation, Footer } from "./components";
import PreRaceLoader from "./components/PreRaceLoader"; // New
import FadeIn from './components/FadeIn';
import './index.scss';
import ScrollToTop from './components/ScrollToTop';

function App() {
    const [isLoading, setIsLoading] = useState(true);
    const [scrollPercent, setScrollPercent] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
          const winScroll = document.documentElement.scrollTop;
          const height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
          const scrolled = (winScroll / height) * 100;
          setScrollPercent(scrolled);
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    if (isLoading) {
      return <PreRaceLoader onComplete={() => setIsLoading(false)} />;
    }

    return (
        <div className="main-container">
            <Navigation />

            {/* Vertical Racing Line - Right Side */}
            <div className="racing-line-container">
              <div className="car-indicator" style={{ top: `${scrollPercent}%` }}></div>
            </div>

            <FadeIn transitionDuration={700}>
                <Main/>
                <CareerBento/>
                <ProjectArchive />
                <GridEntry />
            </FadeIn>
            <Footer />
            <ScrollToTop />
        </div>
    );
}
export default App;
