import React, { useEffect, useRef, useState } from 'react';
import './TrackLine.scss';

const TrackLine = () => {
  const pathRef = useRef<SVGPathElement>(null);
  const [carPos, setCarPos] = useState({ x: 50, y: 0, rotation: 90 });

  useEffect(() => {
    const updatePosition = () => {
      if (!pathRef.current) return;

      const path = pathRef.current;
      const pathLength = path.getTotalLength();

      // Calculate scroll percentage (0 to 1)
      const scrollTotal = document.documentElement.scrollHeight - window.innerHeight;
      const scrollPercent = Math.min(Math.max(window.scrollY / scrollTotal, 0), 1);

      // Get exact X and Y on the curve
      const point = path.getPointAtLength(scrollPercent * pathLength);

      // Look slightly ahead to calculate which way the car should face
      const lookAhead = Math.min(pathLength, (scrollPercent + 0.005) * pathLength);
      const nextPoint = path.getPointAtLength(lookAhead);
      const angle = Math.atan2(nextPoint.y - point.y, nextPoint.x - point.x) * (180 / Math.PI);

      setCarPos({ x: point.x, y: point.y, rotation: angle });
    };

    window.addEventListener('scroll', updatePosition);
    window.addEventListener('resize', updatePosition);
    updatePosition(); // Run once on mount

    return () => {
      window.removeEventListener('scroll', updatePosition);
      window.removeEventListener('resize', updatePosition);
    };
  }, []);

  return (
    <div className="track-line-container">
      {/* We use vector-effect="non-scaling-stroke" so the dots 
          don't stretch, even if the path is 5000px long.
      */}
      <svg width="100%" height="100%" viewBox="0 0 100 1000" preserveAspectRatio="none">
        <path
          ref={pathRef}
          className="track-path"
          d="M 10,0 
             C 20,100 80,150 80,250 
             S 20,350 20,450 
             S 80,550 80,650 
             S 20,750 20,850 
             L 50,1000" 
          fill="none"
        />
      </svg>

      <div 
        className="moving-car"
        style={{ 
          left: `${carPos.x}%`, 
          top: `${carPos.y}%`, // Using % here because the SVG is 100% height
          transform: `translate(-50%, -50%) rotate(${carPos.rotation}deg)` 
        }}
      >
        üèéÔ∏è
      </div>
    </div>
  );
};

export default TrackLine;
