import React, { useState, useEffect } from 'react';
import './GridEntry.scss';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';

// Official 2026 Lineups
const f1Data: { [key: string]: string[] } = {
  "McLaren": ["Lando Norris", "Oscar Piastri"],
  "Ferrari": ["Charles Leclerc", "Lewis Hamilton"],
  "Red Bull": ["Max Verstappen", "Isack Hadjar"],
  "Mercedes": ["George Russell", "Kimi Antonelli"],
  "Aston Martin": ["Fernando Alonso", "Lance Stroll"],
  "Audi": ["Nico Hülkenberg", "Gabriel Bortoleto"],
  "Williams": ["Alex Albon", "Carlos Sainz"],
  "Alpine": ["Pierre Gasly", "Franco Colapinto"],
  "Haas": ["Esteban Ocon", "Oliver Bearman"],
  "Racing Bulls": ["Liam Lawson", "Arvid Lindblad"],
  "Cadillac": ["Sergio Pérez", "Valtteri Bottas"]
};

// Mock data for the Wall
const voterData = [
  { id: 1, name: "Shailee Patel", team: "McLaren", driver: "Lando Norris", timestamp: "12:37:06" },
  { id: 2, name: "Alex Chen", team: "Ferrari", driver: "Lewis Hamilton", timestamp: "11:20:15" },
];

const GridEntry = () => {
  const [isWallView, setIsWallView] = useState(false);
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({ name: '', team: '', driver: '' });

  // Logic to detect if we should show the "Wall" (Pit Wall) view
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    if (params.get('view') === 'pitwall') {
      setIsWallView(true);
    }
  }, []);

  const handleTeamChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setFormData({ ...formData, team: e.target.value, driver: '' });
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const openPitWall = () => {
    // Opens the current URL with a parameter in a new tab
    window.open(window.location.origin + '?view=pitwall', '_blank');
  };

  // --- VIEW 1: THE PIT WALL (Dashboard) ---
  if (isWallView) {
    return (
      <div className="pitwall-dashboard">
        <div className="dashboard-header">
          <div className="live-indicator">● LIVE TELEMETRY</div>
          <h1>RACE_CONTROL_WALL</h1>
          <div className="grid-meta">2026_SEASON_PREDICTIONS</div>
        </div>
        <div className="voter-grid">
          {voterData.map((vote) => (
            <div key={vote.id} className="voter-card">
              <div className="card-top">
                <span className="timestamp">{vote.timestamp}</span>
                <span className="team-tag">{vote.team}</span>
              </div>
              <div className="voter-name">{vote.name.toUpperCase()}</div>
              <div className="selection-details">
                <div className="detail-row">
                  <span className="label">PILOT</span>
                  <span className="value">{vote.driver}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // --- VIEW 2: THE GRID ENTRY (Form) ---
  return (
    <div className="grid-entry-container" id="contact">
      <div className="main-content-area">
        <div className="f1-terminal">
          {step === 1 ? (
            <div className="terminal-content">
              <h2 className="term-header">{'>'} MISSION_OBJECTIVE</h2>
              <p className="term-text">
                Engineering is a race for precision. In the 2026 technical era, data determines the winner. 
                Register your prediction below to join the digital pit wall.
              </p>
              <div className="btn-group">
                <button className="btn-primary" onClick={() => setStep(2)}>
                  ENTER TECHNICAL SPECS <ArrowForwardIosIcon style={{ fontSize: '14px' }} />
                </button>
                <button className="btn-secondary" onClick={openPitWall}>
                  VIEW LIVE PIT WALL
                </button>
              </div>
            </div>
          ) : (
            <div className="terminal-content">
              <button className="btn-back" onClick={() => setStep(1)}>
                <ArrowBackIosNewIcon style={{ fontSize: '12px' }} /> BACK
              </button>
              <h2 className="term-header">{'>'} STRATEGIST_REGISTRATION</h2>
              <div className="form-grid">
                <div className="input-group">
                  <label>USER_ID (NAME)</label>
                  <input name="name" value={formData.name} onChange={handleInputChange} />
                </div>
                <div className="input-group">
                  <label>CONSTRUCTOR_SUPPORT (TEAM)</label>
                  <select name="team" value={formData.team} onChange={handleTeamChange}>
                    <option value="">Select Team</option>
                    {Object.keys(f1Data).sort().map(team => (
                      <option key={team} value={team}>{team}</option>
                    ))}
                  </select>
                </div>
                <div className="input-group">
                  <label>PILOT_AUTHORIZATION (DRIVER)</label>
                  <select 
                    name="driver" 
                    value={formData.driver} 
                    onChange={handleInputChange} 
                    disabled={!formData.team}
                  >
                    <option value="">{formData.team ? 'Select Driver' : 'Select Team First'}</option>
                    {formData.team && f1Data[formData.team].map(driver => (
                      <option key={driver} value={driver}>{driver}</option>
                    ))}
                  </select>
                </div>
                <button className="btn-submit" onClick={openPitWall}>
                  Lock Strategy & Deploy
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default GridEntry;
