import React, { useState } from 'react';
import './PostcardCanvas.scss';

interface PostcardData {
  id: number;
  city: string;
  name: string;
  imageUrl: string;
  xPos: number;
  yPos: number;
  rotation: number;
}

const PostcardCanvas = () => {
  const [cards, setCards] = useState<PostcardData[]>([]);
  const [formData, setFormData] = useState({ city: '', name: '', imageUrl: '' });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    if (!formData.city || !formData.name) return;

    const newCard: PostcardData = {
      id: Date.now(),
      city: formData.city,
      name: formData.name,
      // Use a default vintage-style placeholder if no image provided
      imageUrl: formData.imageUrl || 'https://images.unsplash.com/photo-1526772662000-3f88f10405ff?w=400&q=80',
      xPos: Math.random() * 70, // Keeps them mostly on screen
      yPos: Math.random() * 60,
      rotation: (Math.random() - 0.5) * 50 // Random tilt
    };

    setCards([...cards, newCard]);
    // Optional: keep name, just clear city/image for faster multiple entries
    setFormData({ ...formData, city: '', imageUrl: '' });
  };

  const handleReset = () => {
      if(window.confirm("Are you sure you want to clear the canvas?")) {
          setCards([]);
      }
  }

  // The Reusable Card Component
  const CardItem = ({ data, isPreview = false }: { data: any, isPreview?: boolean }) => (
      <div 
        className={`postcard-item ${isPreview ? 'preview-mode' : 'on-canvas'}`}
        style={!isPreview ? {
          left: `${data.xPos}%`,
          top: `${data.yPos}%`,
          transform: `rotate(${data.rotation}deg)`,
          zIndex: Math.floor(data.yPos)
        } : {}}
      >
         <div 
           className="postcard-image" 
           style={{ backgroundImage: `url(${data.imageUrl || 'https://via.placeholder.com/400x600?text=Photo'})` }} 
         />
         <div className="postcard-text">
           <div className="city-name">{data.city || "CITY NAME"}</div>
           <div className="user-name">- {data.name || "Your Name"}</div>
         </div>
      </div>
  );

  return (
    <div className="postcard-app-container">
      <h2 className="app-title">Postcards from Around the World</h2>

      <div className="app-layout">

          {/* LEFT SIDE: The Canvas */}
          <div className="canvas-section">
            <div className="canvas-board">
                {cards.length === 0 && (
                    <div className="canvas-empty-state">
                        The global guestbook is empty.<br/>Be the first to sign!
                    </div>
                )}
                {cards.map((card) => <CardItem key={card.id} data={card} />)}
            </div>
          </div>

          {/* RIGHT SIDE: Sidebar with Tools */}
          <div className="sidebar-section">

              {/* TOP BOX: Inputs */}
              <div className="tool-box">
                  <h3>Create Postcard</h3>
                  <div className="input-group">
                    <input name="city" className="modern-input" placeholder="City Name" 
                           value={formData.city} onChange={handleInputChange} maxLength={18}/>
                    <input name="name" className="modern-input" placeholder="Your Name" 
                           value={formData.name} onChange={handleInputChange} maxLength={15}/>
                    <input name="imageUrl" className="modern-input" placeholder="Image URL (optional)" 
                           value={formData.imageUrl} onChange={handleInputChange}/>
                  </div>
                  <div className="btn-group">
                    <button className="action-btn submit" onClick={handleSubmit} 
                            disabled={!formData.city || !formData.name}>
                        Stick to Board
                    </button>
                    {cards.length > 0 && (
                        <button className="action-btn reset" onClick={handleReset}>
                            Reset Canvas
                        </button>
                    )}
                  </div>
              </div>

              {/* BOTTOM BOX: Live Preview */}
              <div className="tool-box">
                  <h3>Live Preview</h3>
                  <CardItem data={formData} isPreview={true} />
              </div>

          </div>
      </div>
    </div>
  );
};

export default PostcardCanvas;
