import React, { useEffect, useState } from 'react';

const PreRaceLoader = ({ onComplete }: { onComplete: () => void }) => {
  const [progress, setProgress] = useState(0);
  const [status, setStatus] = useState("INITIALIZING SYSTEMS");

  useEffect(() => {
    const interval = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 100) {
          clearInterval(interval);
          setTimeout(onComplete, 500);
          return 100;
        }
        return prev + 2;
      });
    }, 40);

    return () => clearInterval(interval);
  }, [onComplete]);

  useEffect(() => {
    const systemChecks = [
      "CHECKING AERODYNAMICS...",
      "CALIBRATING TELEMETRY...",
      "WARMING UP TYRES...",
      "SYNCING STRATEGY...",
      "READY TO RACE"
    ];
    const index = Math.min(Math.floor(progress / 25), systemChecks.length - 1);
    setStatus(systemChecks[index]);
  }, [progress]);

  return (
    <div style={{
      position: 'fixed', inset: 0, zIndex: 9999,
      backgroundColor: '#1E1E1E', display: 'flex',
      flexDirection: 'column', alignItems: 'center', justifyContent: 'center'
    }}>
      <div style={{ width: '300px' }}>
        <div style={{ 
          display: 'flex', justifyContent: 'space-between', 
          color: '#FF7A1A', fontFamily: 'monospace', fontSize: '10px', marginBottom: '8px' 
        }}>
          <span>{status}</span>
          <span>{progress}%</span>
        </div>
        <div style={{ width: '100%', height: '2px', backgroundColor: 'rgba(255,122,26,0.1)' }}>
          <div style={{ 
            height: '100%', backgroundColor: '#FF7A1A', 
            width: `${progress}%`, transition: 'width 0.1s linear',
            boxShadow: '0 0 10px #FF7A1A'
          }} />
        </div>
      </div>
    </div>
  );
};

export default PreRaceLoader;
