import React, { useEffect, useRef, useState } from 'react';
import './TrackLine.scss';

const TrackLine = () => {
  const pathRef = useRef<SVGPathElement>(null);
  const [carPos, setCarPos] = useState({ x: 0, y: 0, rotation: 0 });

  useEffect(() => {
    const handleScroll = () => {
      if (!pathRef.current) return;

      const path = pathRef.current;
      const pathLength = path.getTotalLength();

      // Calculate scroll percentage
      const scrollTotal = document.documentElement.scrollHeight - window.innerHeight;
      const scrollPercent = window.scrollY / scrollTotal;

      // Find the point on the SVG path corresponding to scroll percentage
      const point = path.getPointAtLength(scrollPercent * pathLength);

      // Calculate rotation to make the car face the direction of the curve
      const nextPoint = path.getPointAtLength(Math.min(pathLength, (scrollPercent + 0.01) * pathLength));
      const angle = Math.atan2(nextPoint.y - point.y, nextPoint.x - point.x) * (180 / Math.PI);

      setCarPos({ x: point.x, y: point.y, rotation: angle });
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial position
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="track-line-container">
      <svg viewBox="0 0 100 1000" preserveAspectRatio="none" className="track-svg">
        {/* The Curved Track Path */}
        <path
          ref={pathRef}
          className="track-path"
          d="M 5,0 
             Q 15,100 5,200 
             T 5,400 
             C 40,500 -20,600 5,750 
             S 20,900 5,1000" 
          fill="none"
        />
      </svg>

      {/* The Driving Car */}
      <div 
        className="moving-car"
        style={{ 
          left: `${carPos.x}%`, 
          top: `${carPos.y}px`, // This will need to be calculated relative to your page height
          transform: `translate(-50%, -50%) rotate(${carPos.rotation}deg)` 
        }}
      >
        üèéÔ∏è
      </div>
    </div>
  );
};

export default TrackLine;
