import React, { useEffect, useState } from 'react';
import SportsEsportsIcon from '@mui/icons-material/SportsEsports'; // Or a custom SVG car
import './TrackLine.scss';

const TrackLine = () => {
  const [scrollProgress, setScrollProgress] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const totalHeight = document.documentElement.scrollHeight - window.innerHeight;
      const progress = (window.scrollY / totalHeight) * 100;
      setScrollProgress(progress);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="track-line-container">
      {/* The thin dotted line */}
      <div className="track-path"></div>

      {/* The moving car icon */}
      <div 
        className="moving-car" 
        style={{ top: `${scrollProgress}%` }}
      >
        <div className="car-icon">üèéÔ∏è</div>
      </div>
    </div>
  );
};

export default TrackLine;
