import React, { useEffect, useState } from 'react';

const PreRaceLoader = ({ onComplete }: { onComplete: () => void }) => {
  const [progress, setProgress] = useState(0);
  const [status, setStatus] = useState("INITIALIZING SYSTEMS");

  useEffect(() => {
    const interval = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 100) {
          clearInterval(interval);
          setTimeout(onComplete, 800); // Slight delay for the "Ready" state
          return 100;
        }
        return prev + 1; // Slower, smoother increment
      });
    }, 30);

    return () => clearInterval(interval);
  }, [onComplete]);

  useEffect(() => {
    const systemChecks = [
      "CHECKING AERODYNAMICS...",
      "CALIBRATING TELEMETRY...",
      "WARMING UP TYRES...",
      "SYNCING STRATEGY...",
      "READY TO RACE"
    ];
    const index = Math.min(Math.floor(progress / 20), systemChecks.length - 1);
    setStatus(systemChecks[index]);
  }, [progress]);

  return (
    <div style={{
      position: 'fixed', inset: 0, zIndex: 9999,
      backgroundColor: '#0F0F0F', display: 'flex', // Darker background for more contrast
      flexDirection: 'column', alignItems: 'center', justifyContent: 'center'
    }}>
      <div style={{ width: '80%', maxWidth: '500px', position: 'relative' }}>

        {/* Status and Progress Text - Increased size for readability */}
        <div style={{ 
          display: 'flex', justifyContent: 'space-between', 
          color: '#FF7A1A', fontFamily: 'monospace', 
          fontSize: '14px', fontWeight: 'bold', marginBottom: '15px',
          letterSpacing: '2px'
        }}>
          <span>{'>'} {status}</span>
          <span>{progress}%</span>
        </div>

        {/* The Track (Background Bar) */}
        <div style={{ 
          width: '100%', height: '4px', 
          backgroundColor: 'rgba(255,122,26,0.1)',
          position: 'relative',
          overflow: 'visible' // Necessary to see the car above the line
        }}>

          {/* The Progress Bar (Active Track) */}
          <div style={{ 
            height: '100%', backgroundColor: '#FF7A1A', 
            width: `${progress}%`, transition: 'width 0.1s linear',
            boxShadow: '0 0 15px rgba(255,122,26,0.5)'
          }} />

          {/* The Moving Car - Positioned absolute relative to the progress */}
          <div style={{
            position: 'absolute',
            left: `${progress}%`,
            top: '-20px', // Sits above the track
            transform: 'translateX(-100%)', // Keeps car behind the progress point
            transition: 'left 0.1s linear',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center'
          }}>
            {/* Simple CSS/SVG Car Shape */}
            <svg width="40" height="20" viewBox="0 0 24 24" fill="#FF7A1A">
              <path d="M18.92 6.01C18.72 5.42 18.16 5 17.5 5h-11c-.66 0-1.21.42-1.42 1.01L3 12v8c0 .55.45 1 1 1h1c.55 0 1-.45 1-1v-1h12v1c0 .55.45 1 1 1h1c.55 0 1-.45 1-1v-8l-2.08-5.99zM6.5 16c-.83 0-1.5-.67-1.5-1.5S5.67 13 6.5 13s1.5.67 1.5 1.5S7.33 16 6.5 16zm11 0c-.83 0-1.5-.67-1.5-1.5s.67-1.5 1.5-1.5 1.5.67 1.5 1.5-.67 1.5-1.5 1.5zM5 11l1.5-4.5h11L19 11H5z"/>
            </svg>
            {/* Speed trail lines */}
            <div style={{
              width: '20px', height: '2px', background: 'linear-gradient(90deg, transparent, #FF7A1A)',
              marginTop: '2px', opacity: progress > 5 ? 1 : 0
            }} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default PreRaceLoader;
