import React, { useState } from 'react';
import './GridEntry.scss';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';

const f1Data: { [key: string]: string[] } = {
  "McLaren": ["Lando Norris", "Oscar Piastri"],
  "Ferrari": ["Charles Leclerc", "Lewis Hamilton"],
  "Red Bull": ["Max Verstappen", "Isack Hadjar"],
  "Mercedes": ["George Russell", "Kimi Antonelli"],
  "Aston Martin": ["Fernando Alonso", "Lance Stroll"],
  "Audi": ["Nico Hülkenberg", "Gabriel Bortoleto"],
  "Williams": ["Alex Albon", "Carlos Sainz"],
  "Alpine": ["Pierre Gasly", "Franco Colapinto"],
  "Haas": ["Esteban Ocon", "Oliver Bearman"],
  "Racing Bulls": ["Liam Lawson", "Arvid Lindblad"],
  "Cadillac": ["Sergio Pérez", "Valtteri Bottas"]
};

const voterData = [
  { id: 1, name: "Shailee", team: "McLaren", driver: "Lando Norris" },
  { id: 2, name: "Alex", team: "Ferrari", driver: "Lewis Hamilton" },
];

const GridEntry = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({ name: '', team: '', driver: '' });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="grid-entry-container" id="contact" style={{ backgroundColor: '#FAFAF8', padding: '100px 10%' }}>
      <div className="main-content-area" style={{ display: 'flex', justifyContent: 'center' }}>
        <div className="f1-terminal" style={{ 
          backgroundColor: '#1E1E1E', width: '100%', 
          maxWidth: step === 3 ? '1200px' : '800px',
          minHeight: '450px', borderRadius: '4px', padding: '40px', position: 'relative',
          boxShadow: '20px 20px 0px #FF7A1A', transition: 'max-width 0.5s ease'
        }}>

          {step === 1 && (
            <div className="terminal-content">
              <h2 className="terminal-header">{'>'} MISSION_OBJECTIVE</h2>
              <p className="objective-text">
                Formula 1 represents the intersection of performance, precision, and relentless optimization—values that strongly influence how I approach software engineering. 
                In F1, success is driven by data, systems thinking, and the pursuit of marginal gains. 
                As a McLaren and Lando Norris fan, I strive to bring that same technical excellence and adaptability into every system I design.
              </p>
              <div style={{ display: 'flex', gap: '20px' }}>
                <button onClick={() => setStep(2)} className="btn-primary">
                  JOIN THE GRID <ArrowForwardIosIcon style={{ fontSize: '14px' }} />
                </button>
                <button onClick={() => setStep(3)} className="btn-secondary">
                  VIEW PIT WALL
                </button>
              </div>
            </div>
          )}

          {step === 2 && (
            <div className="terminal-content">
              <button onClick={() => setStep(1)} className="btn-back">
                <ArrowBackIosNewIcon style={{ fontSize: '12px' }} /> BACK
              </button>
              <h2 className="terminal-header">{'>'} STRATEGIST_REGISTRATION</h2>
              <div style={{ display: 'grid', gap: '20px' }}>
                <div className="input-group">
                  <label>USER_ID (NAME)</label>
                  <input name="name" value={formData.name} onChange={handleInputChange} placeholder="ENTER NAME..." />
                </div>
                <div className="input-group">
                  <label>CONSTRUCTOR (TEAM)</label>
                  <select name="team" value={formData.team} onChange={handleInputChange}>
                    <option value="">SELECT TEAM</option>
                    {Object.keys(f1Data).sort().map(team => <option key={team} value={team}>{team}</option>)}
                  </select>
                </div>
                <div className="input-group">
                  <label>PILOT (DRIVER)</label>
                  <select name="driver" value={formData.driver} onChange={handleInputChange} disabled={!formData.team}>
                    <option value="">{formData.team ? 'SELECT DRIVER' : 'WAITING FOR TEAM...'}</option>
                    {formData.team && f1Data[formData.team].map(driver => <option key={driver} value={driver}>{driver}</option>)}
                  </select>
                </div>
                <button onClick={() => setStep(3)} className="btn-primary">LOCK STRATEGY & DEPLOY</button>
              </div>
            </div>
          )}

          {step === 3 && (
            <div className="terminal-content">
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
                <h2 className="terminal-header">{'>'} PIT_WALL_TELEMETRY</h2>
                <button onClick={() => setStep(1)} className="btn-back">RESET_COMMS</button>
              </div>

              <div className="telemetry-grid">
                {Object.entries(f1Data).map(([team, drivers]) => 
                  drivers.map(driver => {
                    const predictions = voterData.filter(v => v.driver === driver);
                    return (
                      <div key={driver} className="driver-card">
                        <div className="card-header">
                          <span className="team-label">{team}</span>
                          <span className="vote-count">{predictions.length} VOTES</span>
                        </div>
                        <div className="driver-name">{driver.split(' ').pop()?.toUpperCase()}</div>
                        <div className="voter-list">
                          {predictions.length > 0 
                            ? predictions.map(p => p.name).join(', ') 
                            : 'NO DATA RECEIVED'}
                        </div>
                        <div className="data-bar">
                          <div className="fill" style={{ width: predictions.length > 0 ? '40%' : '0%' }}></div>
                        </div>
                      </div>
                    );
                  })
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default GridEntry;
