import React, { useEffect, useState } from "react";
import { Main, CareerBento, ProjectArchive, Navigation, Footer, GridEntry } from "./components";
import PreRaceLoader from "./components/PreRaceLoader";
import FadeIn from './components/FadeIn';
import ScrollToTop from './components/ScrollToTop';
import './index.scss';

function App() {
    const [isLoading, setIsLoading] = useState(true);
    const [scrollPercent, setScrollPercent] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
          const winScroll = document.documentElement.scrollTop;
          const height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
          const scrolled = (winScroll / height) * 100;
          setScrollPercent(scrolled);
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    if (isLoading) {
      return <PreRaceLoader onComplete={() => setIsLoading(false)} />;
    }

    return (
        <div className="main-container">
            {/* 3️⃣ Progressive Speed Indicator (Top Bar) */}
            <div className="speed-indicator-bar" style={{ width: `${scrollPercent}%` }} />

            <Navigation />

            {/* 1️⃣ The Vertical Racing Line */}
            <div className="vertical-racing-line">
              <div className="line-track" />
              <div className="nodes-container">
                <a href="#hero" className={`node ${scrollPercent < 20 ? 'active' : ''}`}><span>01</span></a>
                <a href="#career" className={`node ${scrollPercent >= 20 && scrollPercent < 50 ? 'active' : ''}`}><span>02</span></a>
                <a href="#projects" className={`node ${scrollPercent >= 50 && scrollPercent < 80 ? 'active' : ''}`}><span>03</span></a>
                <a href="#contact" className={`node ${scrollPercent >= 80 ? 'active' : ''}`}><span>04</span></a>
              </div>
            </div>

            <FadeIn transitionDuration={700}>
                <div id="hero"><Main/></div>
                <div id="career"><CareerBento/></div>
                <div id="projects"><ProjectArchive /></div>
                <div id="contact"><GridEntry /></div>
            </FadeIn>

            <Footer />
            <ScrollToTop />
        </div>
    );
}

export default App;
