import React, { useEffect, useState } from "react";
import { Main, CareerBento, ProjectArchive, Navigation, Footer, GridEntry } from "./components";
import PreRaceLoader from "./components/PreRaceLoader";
import FadeIn from './components/FadeIn';
import ScrollToTop from './components/ScrollToTop';
import TrackLine from './components/TrackLine'; // Added the new track line component
import './index.scss';

function App() {
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Reset scroll on load
        window.scrollTo({top: 0, left: 0, behavior: 'smooth'});
    }, []);

    if (isLoading) {
      return <PreRaceLoader onComplete={() => setIsLoading(false)} />;
    }

    return (
        /* Added position relative to ensure the TrackLine positions itself correctly 
           against the entire page height */
        <div className="main-container" style={{ position: 'relative' }}>
            <Navigation />

            {/* The TrackLine runs behind the content to connect sectors */}
            <TrackLine />

            <FadeIn transitionDuration={700}>
                <div id="hero"><Main/></div>
                <div id="career"><CareerBento/></div>
                <div id="projects"><ProjectArchive /></div>
                <div id="contact"><GridEntry /></div>
            </FadeIn>

            <Footer />
            <ScrollToTop />
        </div>
    );
}

export default App;
