import React, { useEffect, useState } from "react";
import { AppBar, Box, Button, CssBaseline, Divider, Drawer, IconButton, List, ListItem, ListItemButton, ListItemText, Toolbar } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import ListIcon from '@mui/icons-material/List';

const drawerWidth = 240;
const navItems = [
  ['Career', 'career'],
  ['Projects', 'projects'],
  ['Design Portfolio', 'portfolio'],
  ['Contact Me', 'contact']
];

function Navigation() {
  const [mobileOpen, setMobileOpen] = useState<boolean>(false);
  const [scrolled, setScrolled] = useState<boolean>(false);

  const handleDrawerToggle = () => setMobileOpen(!mobileOpen);

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (section: string) => {
    if (section === 'portfolio') {
      window.open("https://shaileeportfolio.my.canva.site/p", '_blank');
      return;
    }
    if (section === 'contact') {
      window.open("https://www.linkedin.com/in/shailee-patel/", '_blank');
      return;
    }
    document.getElementById(section)?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar component="nav" elevation={0} className={`navigation-container ${scrolled ? 'scrolled' : ''}`}>
        <Toolbar className='nav-toolbar'>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            sx={{ display: { sm: 'none' }, color: '#2F4156' }}
          >
            <MenuIcon />
          </IconButton>
          <Box sx={{ display: { xs: 'none', sm: 'flex' }, gap: '12px', flex: 1, justifyContent: 'flex-end' }}>
            {navItems.map((item) => (
              <Button key={item[0]} onClick={() => scrollToSection(item[1])} className="nav-item-button">
                {item[0]}
              </Button>
            ))}
          </Box>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="temporary"
        open={mobileOpen}
        onClose={handleDrawerToggle}
        ModalProps={{ keepMounted: true }}
        sx={{ display: { xs: 'block', sm: 'none' }, '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth } }}
      >
        <Box onClick={handleDrawerToggle} sx={{ textAlign: 'center' }}>
          <p className="mobile-menu-top"><ListIcon/> Menu</p>
          <Divider />
          <List>
            {navItems.map((item) => (
              <ListItem key={item[0]} disablePadding>
                <ListItemButton sx={{ textAlign: 'center' }} onClick={() => scrollToSection(item[1])}>
                  <ListItemText primary={item[0]} />
                </ListItemButton>
              </ListItem>
            ))}
          </List>
        </Box>
      </Drawer>
    </Box>
  );
}

export default Navigation;
